/**
 * Enum representing different types of bed transfers
 */
export enum BedTransferType {
  // Clinical Reasons
  CLINICAL_DETERIORATION = 'CLINICAL_DETERIORATION',
  CLINICAL_IMPROVEMENT = 'CLINICAL_IMPROVEMENT',
  SPECIALIZED_CARE_NEEDED = 'SPECIALIZED_CARE_NEEDED',
  
  // Administrative Reasons
  BED_AVAILABILITY = 'BED_AVAILABILITY',
  WARD_REORGANIZATION = 'WARD_REORGANIZATION',
  PATIENT_REQUEST = 'PATIENT_REQUEST',
  
  // Special Cases
  ISOLATION_REQUIRED = 'ISOLATION_REQUIRED',
  INFECTION_CONTROL = 'INFECTION_CONTROL',
  BEHAVIORAL_ISSUES = 'BEHAVIORAL_ISSUES',
  
  // Transfers Between Facilities
  TRANSFER_TO_HIGHER_CARE = 'TRANSFER_TO_HIGHER_CARE',
  TRANSFER_TO_LOWER_CARE = 'TRANSFER_TO_LOWER_CARE',
  HOSPITAL_TRANSFER = 'HOSPITAL_TRANSFER',
  
  // Other
  MAINTENANCE = 'MAINTENANCE',
  RENOVATION = 'RENOVATION',
  
  // Custom
  OTHER = 'OTHER'
}
